import { createPool } from 'mysql2/promise';
import dotenv from 'dotenv';

dotenv.config({ path: './src/env/.env' });

export const pool = createPool({
    host: process.env.DB_HOST,
    user: process.env.DB_USER,
    password: process.env.DB_PASSWORD,
    port: process.env.DB_PORT,
    database: process.env.DB_DATABASE
});

import { pool } from '../db/db.js';

export const create = async (req, res) => {
    try {
        const { dato1, dato2, dato3, n... } = req.body;
        let sql = 'INSERT INTO name_database(columna1,columna2,columna3,n...) VALUES (?,?,?,?)';
        const [rows] = await pool.query(sql, [dato1, dato2, dato3, n...]);
        if (rows.length === 0) {
            res.status(404).json({message: 'No se registro.'});
        } else {
            res.status(200).json({message: 'Registrado exitosamente.'});
        }
    } catch (error) {
        res.status(500).json({ message: error });
    }};
