import { useState } from 'react';

export default function CodeCard({ title, language, code }) {
    const [copied, setCopied] = useState(false);

    const handleCopy = () => {
        navigator.clipboard.writeText(code);
        setCopied(true);
        setTimeout(() => {
            setCopied(false);
        }, 2000);
    };

    return (
        <div className='bg-white rounded-2xl shadow p-5'>
            <h2 className='text-xl font-semibold text-gray-800 mb-2'>{title}</h2>
            <p className='text-sm text-gray-500 mb-4'>Lenguaje: <span className='font-medium'>{language}</span></p>
            <pre className='bg-gray-900 text-green-300 text-sm p-4 rounded overflow-x-auto'>
                <code>{code}</code>
                <button
                    className=' bg-gray-700 text-white text-xs py-1 px-2 roundedhover:bg-gray-600'
                    onClick={handleCopy}
                >
                    {copied ? 'copiado' : 'copiar'}
                </button>
            </pre>
        </div>
    );
}

import React, { useState, useEffect } from 'react';

const Carrusel = ({ imagenes }) => {
  const [indiceActual, setIndiceActual] = useState(0);

  useEffect(() => {
    const intervalo = setInterval(() => {
      setIndiceActual((indiceActual + 1) % imagenes.length);
    }, 3000);
    return () => clearInterval(intervalo);
  }, [indiceActual, imagenes]);

  const irAnterior = () => {
    setIndiceActual((indiceActual - 1 + imagenes.length) % imagenes.length);
  };

  const irSiguiente = () => {
    setIndiceActual((indiceActual + 1) % imagenes.length);
  };

  return (
    <div className='carrusel'>
      <button className='anterior' onClick={irAnterior}>
        Anterior
      </button>
      <img src={imagenes[indiceActual]} alt='Imagen del carrusel' />
      <button className='siguiente' onClick={irSiguiente}>
        Siguiente
      </button>
      <div className='indicadores'>
        {imagenes.map((_, index) => (
          <span
            key={index}
            className={`indicador ${indiceActual === index ? 'activo' : ''}`}
            onClick={() => setIndiceActual(index)}
          />
        ))}
      </div>
    </div>
  );
};

export default Carrusel;

